DELAY_IN_MITUES = 60*4
def get_api():

    API_KEY = environ["API_KEY"]
    API_KEY_SECRET = environ["API_KEY_SECRET"]
    CON_KEY = environ["CON_KEY"]
    CON_SECRET = environ["CON_SECRET"]
    # Authenticate to Twitter
    auth = tweepy.OAuthHandler(API_KEY, API_KEY_SECRET)
    auth.set_access_token(CON_KEY, CON_SECRET)
    # Create API object
    api = tweepy.API(auth)
    return api

api = get_api()
last_time = datetime.datetime.now() - datetime.timedelta(days=30)

while True:
    current_time = datetime.datetime.now()
    delta = current_time - last_time
    if delta.total_seconds() > 60 * DELAY_IN_MITUES:
        tweet_random(api)
        last_time = current_time
    time.sleep(60)
